// App.js
import React from "react";
import "./index.css";
import Card from "./components/Card";

const App = () => {
  const cardsData = [
    { id: 1, photo: "https://images.unsplash.com/photo-1682687982470-8f1b0e79151a?q=80&w=2940&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D", text: "Card 1" },
    { id: 2, photo: "https://images.unsplash.com/photo-1682687982470-8f1b0e79151a?q=80&w=2940&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D", text: "Card 2" },
    { id: 3, photo: "https://images.unsplash.com/photo-1682687982470-8f1b0e79151a?q=80&w=2940&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D", text: "Card 3" },
    { id: 4, photo: "https://images.unsplash.com/photo-1682687982470-8f1b0e79151a?q=80&w=2940&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D", text: "Card 4" },
  ];

  const handleCardClick = (cardNumber) => {
    console.log(`Card ${cardNumber} clicked!`);
  };

  return (
    <div className="app-container">
      <div className="flex-container">
        <img
          src="./logoseaherbs.png"
          alt="Logo Sea Herbs"
          className="logo"
          style={{ width: "400px", height: "auto" }}
        />
        <div className="container">
          <div className="scrollable-container">
            <div className="search-container">
              <input
                type="text"
                placeholder="SEARCH"
                className="input"
                style={{
                  background: "transparent",
                  border: "1px solid black",
                  borderRadius: "11px",
                  outline: "none",
                  width: "700px",
                  height: "50px",
                  fontWeight: "100",
                  fontSize: "24px",
                  transition: "0.8s",
                  padding: "20px",
                  marginTop: "50px",
                  marginBottom: "20px", // Added marginBottom
                  marginLeft: "auto", // Added auto margin
                  marginRight: "auto", // Added auto margin
                  fontFamily: 'Prompt, sans-serif',
                }}
              />
              <img
                src="./searchicon.png"
                alt="Search Icon"
                className="icon"
                style={{
                  width: "30px",
                  position: "absolute",
                  right: "30px", // Adjusted right position
                  top: "50%", // Center vertically
                  transform: "translateY(-50%)", // Center vertically
                }}
              />
            </div>
            <div
              style={{
                display: "flex",
                flexWrap: "wrap",
                gap: "20px",
                justifyContent: "space-between", // Adjusted for 2x2 grid
                padding: "20px", // Added padding for spacing
              }}
            >
              {cardsData.map((card) => (
                <div
                  key={card.id}
                  style={{
                    width: "calc(50% - 10px)", // 50% width with some gap
                    cursor: "pointer",
                    borderRadius: "15px", // Added border-radius
                    overflow: "hidden", // Ensures border-radius is applied
                  }}
                  onClick={() => handleCardClick(card.id)}
                >
                  <img
                    src={card.photo}
                    alt={`Card ${card.id}`}
                    style={{
                      width: "100%",
                      height: "auto",
                      borderRadius: "15px", // Added border-radius
                    }}
                  />
                  <p>{card.text}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
