// App.js
import React from "react";
import "./index.css";

const Home = () => {


  return (
    <div className="app-container">
      <div className="flex-container">
        <img
          src="./logoseaherbs.png"
          alt="Logo Sea Herbs"
          className="logo"
          style={{ width: "400px", height: "auto" }}
        />
        <div className="container">
          <div className="scrollable-container">
            <div className="search-container" style={{ textAlign: "center" }}>
              <input
                type="text"
                placeholder="SEARCH"
                className="input"
                style={{
                  background: "transparent",
                  border: "1px solid black",
                  borderRadius: "11px",
                  outline: "none",
                  width: "700px",
                  height: "50px",
                  fontWeight: "100",
                  fontSize: "24px",
                  transition: "0.8s",
                  padding: "20px",
                  marginTop: "50px",
                  marginBottom: "20px",
                  fontFamily: 'Prompt, sans-serif',
                }}
              />
            </div>
            <div
              style={{
                display: "flex",
                gap: "5px", 
                justifyContent: "center",
              }}
            ><p style={{
              fontWeight: "300",
              fontSize: "24px",
              fontFamily: 'Prompt, sans-serif',
            }}>Popular Search</p>

            </div>
            <div
              style={{
                display: "flex",
                flexWrap: "wrap",
                gap: "5px", 
                justifyContent: "center",
                padding: "20px",
              }}
            >

              {cardsData.map((card) => (
                <div
                  key={card.id}
                  style={{
                    width: "calc(40% - 5px)",
                    cursor: "pointer",
                    borderRadius: "15px",
                    overflow: "hidden",
                  }}
                  onClick={() => handleCardClick(card.id)}
                >
                  <img
                    src={card.photo}
                    alt={`Card ${card.id}`}
                    style={{
                      width: "100%",
                      height: "300px", 
                      objectFit: "cover", 
                      borderRadius: "15px",
                    }}
                  />
                  <p>{card.text}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
