import React, { useState, useEffect } from "react";
import "./index.css";

const App = () => {
  const [showText, setShowText] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      // You can adjust the scroll threshold as needed
      const scrollThreshold = 200;

      if (scrollPosition > scrollThreshold) {
        setShowText(true);
      } else {
        setShowText(false);
      }
    };

    window.addEventListener("scroll", handleScroll);

    // Cleanup the event listener on component unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div className="app-container">
      <div className="flex-container">
        <img
          src="./logoseaherbs.png"
          alt="Logo Sea Herbs"
          className="logo"
        />
        {showText && (
          <p className="text">
            SEAHERBS หรือย่อมาจาก SEARCH HERBS เป็นเว็บไซต์(Website) ที่รวบรวมข้อมูลสมุนไพร ให้ผู้ใช้ที่สนใจได้เข้ามาค้นหาข้อมูลอย่างอิสระ (Open Data) ผ่านระบบค้นหาข้อมูลเชิงความหมาย (Semantic Search) ที่พัฒนามาจากการสร้างองค์ความรู้ทางสมุนไพร (Ontology)
          </p>
        )}
      </div>
    </div>
  );
};

export default App;
