import React, { useState } from "react";
import "./index.css";

import SearchPage from "./page/searchpage"; // Correct the import statement

const App = () => {
  const [currentPage, setCurrentPage] = useState(1);

  const handleLogoClick = () => {
    setCurrentPage(1);
  };

  return (
    <div className="app-container">
      <div className="flex-container">
        <img
          src="./logoseaherbs.png"
          alt="Logo Sea Herbs"
          className="logo"
          onClick={handleLogoClick}
        />
      </div>
      {currentPage === 1 && <SearchPage />} {/* Correct the component name */}
    </div>
  );
};

export default App;
